using Microsoft.AspNetCore.Mvc;
using Rest.Dtos;

namespace Rest.Controllers
{
    [ApiController]
    [Route("[controller]")]
    public class GreeterController : Controller
    {
        private static string LargeResponse = "";

        public GreeterController()
        {
        }

        [HttpPost("say-hello")]
        public IActionResult SayHello(HelloRequestDto request)
        {
            return Ok(new HelloResponseDto {
                Message = $"Hello {request.Name} from Res"
            });
        }

        [HttpPost("large-say-hello")]
        public IActionResult LagerSayHello(HelloRequestDto request)
        {
            return Ok(new HelloResponseDto
            {
                Message = LargeResponse
            });
        }

        [HttpPost("multiple-fields")]
        public IActionResult MultipleFields(MultipleFieldsRequest request)
        {
            return Ok(new MultipleFieldsResponse
            {
                Prop1 = "teste---",
                Prop2 = 780784,
                Prop3 = "teste---",
                Prop4 = "teste---",
                Prop5 = "teste---",
                Prop6 = "teste---",
                Prop7 = "teste---",
                Prop8 = 878048040,
                Prop9 = "teste---",
                Prop10 = "teste---"
            });
        }

    }
}
